import { useState } from "react";

export default function Header() {
  const [open, setOpen] = useState(false);

  return (
    <header className=" top-0 left-0 w-full z-50 backdrop-blur-xl bg-white/60 border-b border-white/30 shadow-sm">
      <div className="max-w-6xl mx-auto px-5 py-3 flex items-center justify-between">

        {/* LOGO SECTION */}
        <div className="flex items-center gap-3">
          <div className="w-11 h-11 rounded-full bg-accent flex items-center justify-center text-white font-bold text-lg shadow">
            J
          </div>
          <div>
            <div className="text-xl font-extrabold text-strong tracking-wide">
              Jyoti's Parlour
            </div>
            <div className="text-xs text-gray-600 -mt-0.5 tracking-wide">
              Beauty • Hair • Mehendi
            </div>
          </div>
        </div>

        {/* DESKTOP NAV */}
        <nav className="hidden md:flex gap-8 text-gray-700 font-medium">
          <a href="#services" className="nav-link">Services</a>
          <a href="#contact" className="nav-link">Contact</a>
        </nav>

        {/* MOBILE MENU BUTTON */}
        <button 
          className="md:hidden text-2xl text-strong"
          onClick={() => setOpen(!open)}
        >
          {open ? "✖" : "☰"}
        </button>
      </div>

      {/* MOBILE MENU DROPDOWN */}
      {open && (
        <div className="md:hidden bg-white/90 backdrop-blur-xl border-t border-gray-200 shadow-lg">
          <div className="flex flex-col px-6 py-4 gap-4 text-gray-700 font-medium">
            <a onClick={() => setOpen(false)} href="#services" className="nav-mobile">Services</a>
            <a onClick={() => setOpen(false)} href="#contact" className="nav-mobile">Contact</a>
          </div>
        </div>
      )}
    </header>
  );
}
