import { useEffect, useState } from 'react';
const slides = ['../im','/assets/hero2.svg','/assets/hero3.svg'];
export default function Hero(){
  const [idx,setIdx] = useState(0);
  useEffect(()=>{
    const t = setInterval(()=> setIdx(i => (i+1) % slides.length), 4500);
    return ()=> clearInterval(t);
  },[]);
  return (
    <section id="home" className="relative h-72 md:h-96 overflow-hidden">
      {slides.map((s,i)=> (
        <div key={i} className={'absolute inset-0 bg-center bg-cover hero-slide ' + (i===idx ? 'opacity-100 scale-100' : 'opacity-0 scale-105')} style={{ backgroundImage: `url(${s})` }} aria-hidden={i!==idx} />
      ))}
      <div className="relative z-10 max-w-6xl mx-auto px-4 h-full flex items-center">
        <div className="bg-white/85 backdrop-blur-sm rounded-xl p-6 md:p-10 shadow-strong border">
          <h1 className="text-2xl md:text-3xl font-extrabold text-strong">Khubsurati ka naya andaaz</h1>
          <p className="mt-2 text-gray-700">Hair cutting • Beauty makeup • Mehendi — seedha WhatsApp se book karein.</p>
          <a href="#services" className="inline-block mt-4 px-4 py-2 bg-accent text-white rounded-md shadow hover:opacity-95">Services dekhein</a>
        </div>
      </div>
    </section>
  );
}
