// src/components/Services.jsx
import React from "react";
import ServiceItem from "./ServiceItem";
import services from "../data/services"; // your data file

// import fallback images (from src/img or public/assets)
import imgFacial from "../img/facial.png";
import imgMakeup from "../img/makeup.png";
import imgHair from "../img/hair.png";

// mapping by section id -> fallback image
const fallbackMap = {
  hair: "/assets/hairSpa.jpg", // or imgHair if you imported from src/img
  "hair styles": "/assets/outturn.jpg", // make sure id matches services.js (note the space)
  wax: "/assets/babyCut.jpg",
  makeup: "/assets/makeup.png", // use /assets or imported path
  mehendi: "/assets/funkyLook.jpg",
};

export default function Services() {
  return (
    <section id="services" className="max-w-6xl mx-auto px-6 py-12">
      <div className="flex items-center justify-between mb-8">
        <h2 className="text-2xl font-bold text-strong">Our Services</h2>
        <p className="text-sm text-gray-500">Choose from hair, makeup & mehendi â€” book directly on WhatsApp</p>
      </div>

      <div className="grid gap-6">
        {services.map((svc) => (
          <ServiceItem key={svc.id} service={svc} fallbackImg={fallbackMap[svc.id] || "/assets/hairSpa.jpg"} />
        ))}
      </div>
    </section>
  );
}
