// src/components/Services.jsx
import { Link } from "react-router-dom";
import services from "../data/services";

// Fallback icons/images for category banners
import imgHair from "../img/hair.png";
import imgMakeup from "../img/makeup.png";
import imgFacial from "../img/facial.png";

const fallbackMap = {
  hair: imgHair,
  "hair styles": imgHair,
  wax: imgFacial,
  makeup: imgMakeup,
  mehendi: imgFacial,
};

export default function Services() {
  return (
    <section id="services" className="max-w-6xl mx-auto px-6 py-12">
      <div className="flex items-center justify-between mb-8">
        <h2 className="text-2xl font-bold text-strong">Our Services</h2>
        <p className="text-sm text-gray-500">
          Choose from hair, styling, waxing, facial, bridal & mehendi services.
        </p>
      </div>

      <div className="grid gap-8">
        {services.map((service) => (
          <div
            key={service.id}
            className="bg-white border rounded-xl px-6 py-5 shadow-sm hover:shadow-md transition"
          >
            <div className="flex items-center gap-5">
              {/* Category Image */}
              <img
                src={fallbackMap[service.id] || imgHair}
                className="w-20 h-20 rounded-lg object-cover"
                alt={service.title}
              />

              {/* Title + Subtitle */}
              <div className="flex-1">
                <h3 className="text-xl font-semibold text-strong">{service.title}</h3>
                <p className="text-sm text-gray-600">{service.subtitle}</p>
              </div>

              {/* View Details Button */}
              <Link
                to={`/service/${encodeURIComponent(service.id)}`}
                className="px-4 py-2 bg-accent text-white rounded-lg hover:opacity-90"
              >
                View Details
              </Link>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
