// src/components/ServiceItem.jsx
import React, { useRef, useState, useEffect } from "react";
import { WHATSAPP_NUMBER } from "../config";

export default function ServiceItem({ service, fallbackImg }) {
  const { title, subtitle, items } = service;
  const [open, setOpen] = useState(false);
  const panelRef = useRef(null);

  useEffect(() => {
    if (!panelRef.current) return;
    if (open) panelRef.current.style.maxHeight = panelRef.current.scrollHeight + 24 + "px";
    else panelRef.current.style.maxHeight = "0px";
  }, [open]);

  function openWhatsApp(item) {
    const text = `Namaste, main booking karna chahta/chahti hoon: *${item.name}* (Price: ₹${item.price}). Duration: ${item.time}. Kripya available slots bataiye.`;
    if (!WHATSAPP_NUMBER || WHATSAPP_NUMBER.includes("X")) {
      alert("WhatsApp number set nahi hua — src/config.js me WHATSAPP_NUMBER variable me phone number daal dijiye.");
      return;
    }
    const url = `https://wa.me/${WHATSAPP_NUMBER}?text=${encodeURIComponent(text)}`;
    window.open(url, "_blank");
  }

  return (
    <section className="bg-white rounded-xl border p-4 shadow-sm">
      <div className="flex items-center gap-4">
        {/* left image (fallback for the whole section) */}
        {fallbackImg ? (
          <img
            src={fallbackImg}
            alt={title}
            className="w-20 h-20 object-cover rounded-md flex-shrink-0"
            loading="lazy"
          />
        ) : (
          <div className="w-20 h-20 bg-green-50 rounded-md flex items-center justify-center text-green-700">Img</div>
        )}

        <div className="flex-1">
          <h3 className="text-lg font-semibold text-strong">{title}</h3>
          {subtitle && <p className="text-sm text-gray-500 mt-1">{subtitle}</p>}
        </div>

        <div className="flex items-center gap-3">
          <button
            onClick={() => setOpen((s) => !s)}
            className="px-3 py-2 bg-accent text-white rounded-md hover:opacity-95 transition"
            aria-expanded={open}
            aria-controls={`panel-${service.id}`}
          >
            {open ? "Hide Details" : "View Details"}
          </button>
        </div>
      </div>

      {/* Expandable panel */}
      <div
        id={`panel-${service.id}`}
        ref={panelRef}
        className="overflow-hidden transition-[max-height] duration-300 ease-[cubic-bezier(.2,.9,.3,1)] mt-4"
        style={{ maxHeight: 0 }}
      >
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {items.map((it, idx) => {
            // item-level image: prefer item.img, fallback to section fallbackImg
            let imgSrc = it.img || fallbackImg || "";
            // normalize relative paths if some items use "../img/name.jpg"
            if (imgSrc.startsWith("../img/") || imgSrc.startsWith("./img/")) {
              const name = imgSrc.split("/").pop();
              imgSrc = `/assets/${name}`;
            } else if (imgSrc && !imgSrc.startsWith("/")) {
              // if someone used "assets/name.jpg" -> ensure leading slash
              if (imgSrc.includes("assets/")) imgSrc = `/${imgSrc}`;
            }

            return (
              <article
                key={idx}
                className="bg-white rounded-lg border p-3 flex flex-col h-full hover:shadow-strong transition"
              >
                <div className="flex items-start gap-3">
                  {imgSrc ? (
                    <img
                      src={imgSrc}
                      alt={it.name}
                      className="w-28 h-20 object-cover rounded-md flex-shrink-0"
                      loading="lazy"
                      onError={(e) => {
                        e.currentTarget.onerror = null;
                        e.currentTarget.src =
                          "data:image/svg+xml;utf8," +
                          encodeURIComponent(
                            `<svg xmlns='http://www.w3.org/2000/svg' width='400' height='300'><rect width='100%' height='100%' fill='#f3faf3'/><text x='50%' y='50%' dominant-baseline='middle' text-anchor='middle' fill='#29411f' font-size='16'>Image</text></svg>`
                          );
                        e.currentTarget.classList.add("object-contain");
                      }}
                    />
                  ) : (
                    <div className="w-28 h-20 bg-green-50 rounded-md flex items-center justify-center text-green-700">No Img</div>
                  )}

                  <div className="flex-1">
                    <div className="flex items-center justify-between">
                      <h4 className="font-medium text-gray-900">{it.name}</h4>
                      <div className="text-accent font-semibold">₹{it.price}</div>
                    </div>

                    {it.dis && <p className="text-sm text-gray-600 mt-2">{it.dis}</p>}

                    <div className="mt-2 text-sm text-gray-500 flex items-center gap-2">
                      <span>⏳ {it.time}</span>
                    </div>
                  </div>
                </div>

                <div className="mt-3">
                  <button
                    onClick={() => openWhatsApp(it)}
                    className="w-full px-3 py-2 mt-2 bg-strong text-white rounded-md"
                    aria-label={`Book ${it.name} on WhatsApp`}
                  >
                    Book on WhatsApp
                  </button>
                </div>
              </article>
            );
          })}
        </div>
      </div>
    </section>
  );
}
