import { useParams, Link } from 'react-router-dom';
import services from '../data/services';
import { WHATSAPP_NUMBER } from '../config';

export default function ServiceDetail(){
  const { key } = useParams();
  const service = services.find(s=>s.key===key);
  if(!service) return <div className="max-w-4xl mx-auto p-6">Service not found</div>

  function book(it){
    const message = `‡§®‡§Æ‡§∏‡•ç‡§§‡•á üôè%0A%0A*‡§¨‡•Å‡§ï‡§ø‡§Ç‡§ó ‡§Ö‡§®‡•Å‡§∞‡•ã‡§ß*%0A*‡§∏‡•á‡§µ‡§æ:* ${it.name}%0A*‡§ï‡•Ä‡§Æ‡§§:* ‚Çπ${it.price}%0A%0A‡§ï‡•É‡§™‡§Ø‡§æ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§∏‡•ç‡§≤‡•â‡§ü ‡§¨‡§§‡§æ‡§á‡§è‡•§`;
    window.open(`https://api.whatsapp.com/send?phone=${WHATSAPP_NUMBER}&text=${message}`, '_blank');
  }

  return (
    <div className="max-w-6xl mx-auto px-6 py-12">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-bold text-strong">{service.title}</h1>
          <p className="text-sm text-gray-500">{service.subtitle}</p>
        </div>
        <Link to="/" className="text-accent underline">‚Üê Back</Link>
      </div>

      <div className="grid gap-6 md:grid-cols-3">
        {service.items.map((it,idx)=>(
          <div key={idx} className="bg-white rounded-xl border p-6 shadow-sm">
            <div className="text-lg font-semibold text-gray-800">{it.name}</div>
            <div className="text-accent font-bold mt-2">‚Çπ{it.price}</div>
            <p className="text-sm text-gray-500 mt-2">Short description about the style or time required.</p>
            <button onClick={()=>book(it)} className="mt-4 w-full px-4 py-2 bg-strong text-white rounded-md">Book on WhatsApp</button>
          </div>
        ))}
      </div>
    </div>
  )
}
