import { useEffect, useState, useContext } from "react";
import { LanguageContext } from "../context/LanguageContext";

export default function Header() {
  const [open, setOpen] = useState(false);
  const { lang, setLang } = useContext(LanguageContext);

  return (
    <header className="sticky top-0 w-full z-50 bg-white/60 backdrop-blur-xl border-b shadow-sm">
      <div className="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">

        {/* Logo Section */}
        <a href="/" className="flex items-center gap-3">
          <div className="w-11 h-11 rounded-full bg-accent text-white flex items-center justify-center font-bold">J</div>
          <div>
            <div className="text-xl font-extrabold text-strong">Jyoti's Parlour</div>
            <div className="text-xs text-gray-600">Beauty ‚Ä¢ Hair ‚Ä¢ Mehendi</div>
          </div>
        </a>

        {/* Desktop Nav */}
        <nav className="hidden md:flex items-center gap-6 text-gray-700 font-medium">
          <a href="#services" className="nav-link">Services</a>
          <a href="#contact" className="nav-link">Contact</a>

          {/* üåê Language Switcher */}
          <select
            value={lang}
            onChange={(e) => setLang(e.target.value)}
            className="px-2 py-1 border rounded-md bg-white text-gray-700"
          >
            <option value="en">English</option>
            <option value="hi">Hindi</option>
            <option value="gu">Gujarati</option>
          </select>
        </nav>

        {/* Mobile Menu Button */}
        <button className="md:hidden text-2xl" onClick={() => setOpen(!open)}>
          {open ? "‚úñ" : "‚ò∞"}
        </button>
      </div>

      {/* Mobile Menu */}
      {open && (
        <div className="md:hidden bg-white/95 p-4 border-t shadow">
          <a href="#services" className="nav-mobile" onClick={() => setOpen(false)}>Services</a>
          <a href="#contact" className="nav-mobile" onClick={() => setOpen(false)}>Contact</a>

          {/* Language on mobile */}
          <div className="mt-3">
            <select
              value={lang}
              onChange={(e) => setLang(e.target.value)}
              className="w-full px-3 py-2 border rounded-md bg-white"
            >
              <option value="en">English</option>
              <option value="hi">Hindi</option>
              <option value="gu">Gujarati</option>
            </select>
          </div>
        </div>
      )}
    </header>
  );
}
