import { Link } from 'react-router-dom';
import services from '../data/services';

export default function Services(){
  return (
    <section id='services' className='max-w-6xl mx-auto px-4 py-12'>
      <h2 className='text-2xl font-bold mb-6 text-strong'>Humare Services</h2>
      <div className='grid gap-6 md:grid-cols-3 sm:grid-cols-2'>
        {services.map(s => (
          <article key={s.key} className='bg-white rounded-xl border overflow-hidden transform transition hover:-translate-y-2 hover:shadow-strong'>
            {/* Header clickable: opens new page */}
            <div className='w-full text-left px-5 py-4 flex items-center justify-between'>
              <div>
                <h4 className='text-lg font-semibold text-strong'>{s.title}</h4>
                <p className='text-sm text-gray-500 mt-1'>{s.subtitle}</p>
              </div>
              <Link to={`/service/${s.key}`} className='text-accent font-bold text-2xl px-3 py-1 rounded hover:bg-accent/10'>â€º</Link>
            </div>

            {/* Optional: small preview area */}
            <div className='px-5 pb-4'>
              <div className='mt-3 text-sm text-gray-400'>Click arrow to view all {s.title} styles & prices</div>
            </div>
          </article>
        ))}
      </div>

      <div id='contact' className='mt-10 text-gray-700'>
        <h3 className='font-semibold text-lg text-strong'>Contact</h3>
        <p className='mt-1'>Phone / WhatsApp: <strong>+91 XXXXXXXXXX</strong></p>
      </div>
    </section>
  );
}
