// src/pages/ServiceDetail.js
import { useParams, Link, useNavigate } from 'react-router-dom';
import services from '../data/services';
import { WHATSAPP_NUMBER } from '../config';

export default function ServiceDetail() {
  const { key } = useParams();
  const navigate = useNavigate();
  const service = services.find(s => s.key === key);

  if (!service) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-12">
        <h2 className="text-xl font-bold">Service nahi mila</h2>
        <p className="mt-2">Aap <button onClick={() => navigate('/')} className="text-accent underline">home</button> par wapas ja sakte hain.</p>
      </div>
    );
  }

  function book(it) {
    const message =
      `Namaste üôè

*BUKING ANURODH*
*Seva:* ${it.name}
*Kimat:* ‚Çπ${it.price}

Kripya available slots bataiye. Dhanyavaad.`;

    const encoded = encodeURIComponent(message);

    const url = `https://api.whatsapp.com/send?phone=${WHATSAPP_NUMBER}&text=${encoded}`;

    console.log("FINAL URL:", url);
    window.open(url, "_blank");
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-12">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-bold text-strong">{service.title}</h1>
          <p className="text-sm text-gray-500">{service.subtitle}</p>
        </div>
        <div>
          <Link to="/" className="text-accent underline">‚Üê Back to Services</Link>
        </div>
      </div>

      {/* Grid of item boxes */}
      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3">
        {service.items.map((it, i) => (
          <div key={i} className="bg-white rounded-xl border p-4 shadow-sm hover:shadow-strong transition">
            <div className="flex justify-between items-start">
              <div>
                <div className="text-lg font-semibold text-gray-800">{it.name}</div>
                <div className="text-accent font-bold mt-2">‚Çπ{it.price}</div>
                <p className="text-sm text-gray-500 mt-2">Yahaan chhota description daal sakte hain agar chahiye.</p>
              </div>
            </div>

            <div className="mt-4">
              <button onClick={() => book(it)} className="w-full px-4 py-2 bg-strong text-white rounded-md">Book on WhatsApp</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
