import { useEffect, useState, useRef } from 'react';
import img1 from '../img/img-1.jpg'; // import from src/img/
import img2 from '../img/img-2.jpg';
import img3 from '../img/img-3.jpg';
const slides = [img1, img2, img3];

export default function Hero(){
  const [idx,setIdx] = useState(0);
  const tRef = useRef(null);

  useEffect(()=> {
    tRef.current = setInterval(()=> setIdx(i => (i+1)%slides.length), 4500);
    return ()=> clearInterval(tRef.current);
  },[]);

  return (
    <section className="relative h-[62vh] sm:h-[72vh] md:h-[78vh] overflow-hidden">
      {slides.map((s,i)=>(
        <div key={i}
          className={`absolute inset-0 bg-center bg-cover hero-slide ${i===idx?'opacity-100 scale-100 z-10':'opacity-0 scale-105 z-0'}`}
          style={{ backgroundImage:`url(${s})` }}
          aria-hidden={i!==idx}
        />
      ))}
      <div className="absolute inset-0 bg-gradient-to-b from-black/20 via-black/10 to-black/40" />

      <div className="relative z-20 max-w-6xl mx-auto px-6 py-8 h-full flex items-center">
        <div className="w-full md:w-2/3 lg:w-1/2 bg-white/90 backdrop-blur-sm rounded-xl p-6 md:p-10 shadow-strong border">
          <h1 className="text-2xl md:text-4xl font-extrabold text-strong">
            Khubsurati ka <span className="text-accent">naya andaaz</span>
          </h1>
          <p className="mt-3 text-gray-700">Professional haircuts, makeup & mehendi â€” warm service, expert care.</p>

          <div className="mt-5 flex flex-col sm:flex-row gap-3">
            <a href="#services" className="inline-flex items-center justify-center px-5 py-3 bg-accent text-white rounded-md font-medium shadow hover:opacity-95">
              Explore Services
            </a>
            <a href={`https://api.whatsapp.com/send?phone=918780439762&text=${encodeURIComponent('à¤¨à¤®à¤¸à¥à¤¤à¥‡ ðŸ™%0A%0Aà¤®à¥ˆà¤‚ à¤‰à¤ªà¤²à¤¬à¥à¤§à¤¤à¤¾ à¤ªà¥‚à¤›à¤¨à¤¾ à¤šà¤¾à¤¹à¤¤à¤¾/à¤šà¤¾à¤¹à¤¤à¥€ à¤¹à¥‚à¤.')}`} 
               className="inline-flex items-center justify-center px-5 py-3 border border-strong rounded-md text-strong font-medium hover:bg-strong hover:text-white transition"
               target="_blank" rel="noreferrer">
              Book on WhatsApp
            </a>
          </div>
        </div>
      </div>

      {/* bottom indicators */}
      <div className="absolute left-1/2 -translate-x-1/2 bottom-4 z-30 flex gap-2">
        {slides.map((_,i)=>(
          <button key={i} onClick={()=>setIdx(i)} className={`w-3 h-3 rounded-full ${i===idx?'bg-strong':'bg-white/60'}`} />
        ))}
      </div>
    </section>
  )
}
