import ServiceItem from "./ServiceItem";
import services from "../data/services";

// Import images from src/img/ (or use public path '/img/...')
import imgFacial from "../img/facial.png";
import imgMakeup from "../img/makeup.png";
import imgHair from "../img/hair.png";

export default function Services(){
  const mapping = [
    { id: "hair", img: imgHair },
    {id: "hair style", img:imgHair },
    { id: "makeup", img: imgMakeup },
    { id: "mehendi", img: imgFacial } // change to appropriate image
  ];

  return (
    <section id="services" className="max-w-6xl mx-auto px-6 py-12">
      <div className="flex items-center justify-between mb-8">
        <h2 className="text-2xl font-bold text-strong">Our Services</h2>
        <p className="text-sm text-gray-500">Choose from hair, makeup & mehendi â€” book directly on WhatsApp</p>
      </div>

      <div className="grid gap-8">
        {mapping.map(m => {
          const svc = services.find(s=>s.id===m.id);
          if(!svc) return null;
          return <ServiceItem key={m.id} service={svc} img={m.img} />;
        })}
      </div>
    </section>
  );
}
