import React from "react";
import { Link } from "react-router-dom";
import { WHATSAPP_NUMBER } from "../config";

// props: { service, img }
export default function ServiceItem({ service, img }) {
  function quickWhatsApp() {
    const msg = `‡§®‡§Æ‡§∏‡•ç‡§§‡•á üôè%0A%0A‡§Æ‡•à‡§Ç ${service.title} ‡§ï‡•á ‡§¨‡§æ‡§∞‡•á ‡§Æ‡•á‡§Ç ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä ‡§ö‡§æ‡§π‡§§‡•Ä/‡§ö‡§æ‡§π‡§§‡§æ ‡§π‡•Ç‡§Å.%0A%0A‡§ï‡•É‡§™‡§Ø‡§æ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§∏‡•ç‡§≤‡•â‡§ü ‡§¨‡§§‡§æ‡§á‡§è.`;
    window.open(`https://api.whatsapp.com/send?phone=${WHATSAPP_NUMBER}&text=${msg}`, "_blank");
  }

  return (
    <div className="flex flex-col md:flex-row items-stretch gap-4">
      {/* Image side */}
      <div
        className="md:w-1/3 w-full h-44 md:h-auto rounded-lg overflow-hidden shrink-0"
        style={{ backgroundImage: `url(${img})`, backgroundSize: "cover", backgroundPosition: "center" }}
        aria-hidden
      />

      {/* Card side */}
      <div className="md:w-2/3 w-full bg-white/90 backdrop-blur-sm rounded-xl p-6 shadow-2xl border">
        <div className="flex items-start gap-4">
          <div className="w-12 h-12 rounded-md bg-gray-100 flex items-center justify-center text-accent font-semibold text-lg">
            {service.title[0]}
          </div>
          <div className="flex-1">
            <h3 className="text-lg font-semibold text-strong">{service.title}</h3>
            <p className="text-sm text-gray-500 mt-1">{service.subtitle}</p>
          </div>
        </div>

        <div className="mt-6 space-y-3">
          {service.items.map((it, i) => (
            <div key={i} className="flex items-center justify-between">
              <div className="text-gray-800">{it.name}</div>
              <div className="text-accent font-semibold">‚Çπ{it.price}</div>
            </div>
          ))}
        </div>

        <div className="mt-6 flex items-center gap-3">
          <Link to={`/service/${service.id}`} className="px-4 py-2 bg-strong text-white rounded-md">View Styles</Link>
          <button onClick={quickWhatsApp} className="px-4 py-2 border rounded-md text-strong">Quick WhatsApp</button>
        </div>
      </div>
    </div>
  );
}
