import { WHATSAPP_NUMBER } from "../config";

export default function WixServiceCard({ title, color = "blush", image }) {

  function bookNow() {
    const msg = `Namaste üôè\n\n*Service:* ${title}\n\nPlease share available slots.`;
    const url = `https://api.whatsapp.com/send?phone=${WHATSAPP_NUMBER}&text=${encodeURIComponent(msg)}`;
    window.open(url, "_blank");
  }

  return (
    <div className="relative w-full h-[260px] md:h-[320px] overflow-hidden flex shadow-sm">

      {/* LEFT SIDE (Color Section) */}
      <div className={`w-1/2 flex flex-col justify-center items-center bg-${color} p-4`}>
        <div className="w-8 h-0.5 bg-dark mb-4"></div>

        <h2 className="text-xl md:text-2xl font-semibold text-dark tracking-wide">
          {title}
        </h2>

        <button
          onClick={bookNow}
          className="mt-6 text-sm tracking-wide text-dark underline-offset-2 hover:underline"
        >
          BOOK NOW &gt;
        </button>
      </div>

      {/* RIGHT SIDE (Image Section) */}
      <div
        className="w-1/2 bg-cover bg-center"
        style={{ backgroundImage: `url(${image})` }}
      />
    </div>
  );
}
