import { useEffect, useState, useRef } from 'react';
import img1 from '../img/img-1.jpg';
import img2 from '../img/img-2.jpg';
import img3 from '../img/img-3.jpg';

const slides = [img1, img2, img3];

export default function Hero() {
  const [idx, setIdx] = useState(0);
  const timeoutRef = useRef(null);
  const touchStartX = useRef(null);

  // auto slide
  useEffect(() => {
    startAuto();

    const onKey = (e) => {
      if (e.key === 'ArrowLeft') prev();
      if (e.key === 'ArrowRight') next();
    };

    window.addEventListener('keydown', onKey);

    return () => {
      stopAuto();
      window.removeEventListener('keydown', onKey);
    };

    // eslint-disable-next-line
  }, [idx]);

  function startAuto() {
    stopAuto();
    timeoutRef.current = setInterval(() => {
      setIdx((i) => (i + 1) % slides.length);
    }, 4500);
  }
  function stopAuto() {
    if (timeoutRef.current) {
      clearInterval(timeoutRef.current);
      timeoutRef.current = null;
    }
  }
  function prev() {
    setIdx((i) => (i - 1 + slides.length) % slides.length);
  }
  function next() {
    setIdx((i) => (i + 1) % slides.length);
  }

  // touch handlers for mobile swipe
  function onTouchStart(e) {
    stopAuto();
    touchStartX.current = e.touches[0].clientX;
  }
  function onTouchMove(e) {
    // optional: implement if want dragging visuals
  }
  function onTouchEnd(e) {
    startAuto();
    if (!touchStartX.current) return;
    const dx = e.changedTouches[0].clientX - touchStartX.current;
    if (Math.abs(dx) > 40) {
      if (dx > 0) prev();
      else next();
    }
    touchStartX.current = null;
  }

  // WhatsApp quick booking (optional quick action)
  function quickWhatsApp() {
    const WHATSAPP_NUMBER = '918780439762'; // replace in config if you use config
    const message = `‡§®‡§Æ‡§∏‡•ç‡§§‡•á üôè%0A%0A*‡§¨‡•Å‡§ï‡§ø‡§Ç‡§ó ‡§Ö‡§®‡•Å‡§∞‡•ã‡§ß*%0A*‡§∏‡•á‡§µ‡§æ:* Quick Inquiry%0A%0A‡§ï‡•É‡§™‡§Ø‡§æ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§∏‡•ç‡§≤‡•â‡§ü ‡§¨‡§§‡§æ‡§á‡§è‡•§`;
    const url = `https://api.whatsapp.com/send?phone=${WHATSAPP_NUMBER}&text=${message}`;
    window.open(url, '_blank');
  }

  return (
    <section
      id="home"
      className="relative h-[62vh] sm:h-[72vh] md:h-[80vh] overflow-hidden"
      onMouseEnter={stopAuto}
      onMouseLeave={startAuto}
    >
      {/* slides */}
      {slides.map((s, i) => (
        <div
          key={i}
          className={`absolute inset-0 bg-center bg-cover hero-slide transform-gpu transition-all duration-900 ${i === idx ? 'opacity-100 scale-100 z-10' : 'opacity-0 scale-105 z-0'}`}
          style={{ backgroundImage: `url(${s})` }}
          aria-hidden={i !== idx}
        />
      ))}

      {/* dark overlay for contrast */}
      <div className="absolute inset-0 bg-gradient-to-b from-black/30 via-black/10 to-black/40 pointer-events-none" />

      {/* content */}
      <div
        className="relative z-20 max-w-3xl mx-auto px-4 h-full flex flex-col justify-center items-start text-left"
        onTouchStart={onTouchStart}
        onTouchMove={onTouchMove}
        onTouchEnd={onTouchEnd}
      >
        <div className="bg-white/80 backdrop-blur-sm rounded-xl p-4 md:p-6 shadow-strong border">
          <h1 className="text-2xl sm:text-3xl md:text-4xl font-extrabold text-strong leading-tight">
            Khubsurati ka <span className="text-accent">naya andaaz</span>
          </h1>
          <p className="mt-2 text-sm sm:text-base text-gray-700">
            Hair cutting ‚Ä¢ Beauty makeup ‚Ä¢ Mehendi ‚Äî professional care aur warm service.
          </p>

          <div className="mt-4 flex gap-3">
            <a href="#services" className="inline-flex items-center px-4 py-2 bg-accent text-white rounded-md text-sm font-medium shadow hover:opacity-95">
              Services dekhein
            </a>

            <button
              onClick={quickWhatsApp}
              className="inline-flex items-center gap-2 px-3 py-2 bg-strong text-white rounded-md text-sm font-medium"
              aria-label="Quick WhatsApp"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="w-4 h-4" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round" d="M21 11.5a8.38 8.38 0 0 1-1.5 4.9L21 21l-4.6-1.5A8.5 8.5 0 1 1 21 11.5z" />
                <path strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round" d="M16 11c-.2.7-1 1.1-1.8 1.4-.6.2-1.4.3-2.1.1-1.1-.3-1.8-1.1-2.1-2.2-.2-.6-.1-1.4.1-2.1C9.7 7.3 10 6.5 10 5.7 10 4.8 9.6 4.2 9.3 3.9" />
              </svg>
              Quick Book
            </button>
          </div>
        </div>
      </div>

      {/* indicators (dots) */}
      <div className="absolute left-1/2 -translate-x-1/2 bottom-4 z-30 flex gap-2">
        {slides.map((_, i) => (
          <button
            key={i}
            onClick={() => { setIdx(i); startAuto(); }}
            aria-label={`Slide ${i + 1}`}
            className={`w-2.5 h-2.5 rounded-full ${i === idx ? 'bg-strong' : 'bg-white/60'}`}
          />
        ))}
      </div>

      {/* optional prev/next (small on mobile) */}
      <button
        onClick={() => { prev(); startAuto(); }}
        aria-hidden
        className="absolute left-2 top-1/2 -translate-y-1/2 z-30 p-2 bg-black/30 text-white rounded-md md:hidden"
      >
        ‚Äπ
      </button>
      <button
        onClick={() => { next(); startAuto(); }}
        aria-hidden
        className="absolute right-2 top-1/2 -translate-y-1/2 z-30 p-2 bg-black/30 text-white rounded-md md:hidden"
      >
        ‚Ä∫
      </button>
    </section>
  );
}
