// src/components/Hero.js  (or .jsx)
import React, { useEffect, useState } from "react";

const slides = [
  "/assets/img-1.jpg",
  "/assets/img-2.jpg",
  "/assets/img-3.jpg",
];

export default function Hero() {
  const [idx, setIdx] = useState(0);
  useEffect(() => {
    const t = setInterval(() => setIdx(i => (i + 1) % slides.length), 4000);
    return () => clearInterval(t);
  }, []);

  return (
    <section id="home" className="relative h-72 md:h-96 overflow-hidden">
      {slides.map((s, i) => (
        <div
          key={i}
          className={`absolute inset-0 bg-center bg-cover transition-opacity duration-900 ${i === idx ? "opacity-100" : "opacity-0"}`}
          style={{ backgroundImage: `url(${s})` }}
          aria-hidden={i !== idx}
        />
      ))}

      <div className="relative z-10 max-w-6xl mx-auto px-4 h-full flex items-center">
        <div className="bg-white/85 backdrop-blur-sm rounded-xl p-6 md:p-10 shadow-strong border">
          <h1 className="text-2xl md:text-3xl font-extrabold text-strong">A new look of beauty</h1>
          <p className="mt-2 text-gray-700">Hair cutting • Beauty makeup • Mehendi — book directly from WhatsApp.</p>
        </div>
      </div>
    </section>
  );
}
