import { useParams, Link } from "react-router-dom";
import services from "../data/services";
import { WHATSAPP_NUMBER } from "../config";

export default function ServiceDetail() {
  const { id } = useParams();

  // find service block
  const service = services.find((s) => s.id === id);

  if (!service) return <div className="p-6">Service not found</div>;

  function book(item) {
    const msg = `‡§®‡§Æ‡§∏‡•ç‡§§‡•á üôè

*‡§¨‡•Å‡§ï‡§ø‡§Ç‡§ó ‡§Ö‡§®‡•Å‡§∞‡•ã‡§ß*
*‡§∏‡•á‡§µ‡§æ:* ${item.name}
*‡§ï‡•Ä‡§Æ‡§§:* ‚Çπ${item.price}
*‡§∏‡§Æ‡§Ø:* ${item.time}

‡§ï‡•É‡§™‡§Ø‡§æ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§∏‡•ç‡§≤‡•â‡§ü ‡§¨‡§§‡§æ‡§á‡§è‡•§`;

    const url = `https://api.whatsapp.com/send?phone=${WHATSAPP_NUMBER}&text=${encodeURIComponent(msg)}`;
    window.open(url, "_blank");
  }

  return (
    <div className="max-w-5xl mx-auto px-6 py-10">
      <div className="mb-6 flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-strong">{service.title}</h1>
          <p className="text-gray-600">{service.subtitle}</p>
        </div>
        <Link to="/" className="text-accent underline">‚Üê Back</Link>
      </div>

      <div className="grid gap-6 md:grid-cols-2">
        {service.items.map((item, index) => (
          <div key={index} className="bg-white rounded-xl border p-6 shadow-sm">
            <h3 className="text-lg font-semibold text-strong">{item.name}</h3>
            <div className="mt-2 text-accent font-bold text-xl">‚Çπ{item.price}</div>
            <div className="mt-1 text-gray-500 text-sm">‚è≥ Duration: {item.time}</div>

            <button
              onClick={() => book(item)}
              className="mt-4 w-full px-4 py-2 bg-strong text-white rounded-md hover:opacity-90"
            >
              Book on WhatsApp
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
