// src/components/Services.jsx
import React from "react";
import DATA from "../data/services"; // adjust path to where you keep your exported array
import { WHATSAPP_NUMBER } from "../config";


function openWhatsApp(item) {
  const text = `Namaste, main booking karna chahta/chahti hoon: *${item.name}* (Price: ₹${item.price}). Duration: ${item.time}. Kripya available slots bataiye.`;
  if (!WHATSAPP_NUMBER || WHATSAPP_NUMBER.includes("X")) {
    alert("WhatsApp number set nahi hua — src/config.js me WHATSAPP_NUMBER variable me phone number daal dijiye.");
    return;
  }
  const url = `https://wa.me/${WHATSAPP_NUMBER}?text=${encodeURIComponent(text)}`;
  window.open(url, "_blank");
}

export default function Services() {
  return (
    <section id="services" className="max-w-6xl mx-auto px-4 py-12">
      <h2 className="text-3xl font-extrabold mb-8 text-strong">Our Services</h2>

      {/* Sections */}
      <div className="space-y-10">
        {DATA.map((section) => (
          <div key={section.id} className="rounded-lg">
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="text-2xl font-semibold text-strong">{section.title}</h3>
                <p className="text-sm text-gray-500 mt-1">{section.subtitle}</p>
              </div>
            </div>

            {/* items grid */}
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
              {section.items.map((it, idx) => {
                // normalize image path — prefer public/assets
                let imgSrc = it.img || "";
                // if img path starts with "../img" or "./img", try convert to /assets/filename
                if (imgSrc.startsWith("../img/") || imgSrc.startsWith("./img/")) {
                  const name = imgSrc.split("/").pop();
                  imgSrc = `/assets/${name}`;
                }

                return (
                  <article
                    key={idx}
                    className="bg-white rounded-xl border overflow-hidden shadow-sm transform transition hover:-translate-y-2 hover:shadow-strong"
                  >
                    <div className="flex flex-col h-full">
                      {/* image */}
                      {imgSrc ? (
                        <div className="h-44 md:h-36 w-full overflow-hidden bg-gray-100">
                          <img
                            src={imgSrc}
                            alt={it.name}
                            className="w-full h-full object-cover transition-transform duration-500 transform hover:scale-105"
                            loading="lazy"
                            onError={(e) => {
                              // fallback to a neutral placeholder color if image missing
                              e.currentTarget.style.display = "none";
                            }}
                          />
                        </div>
                      ) : (
                        <div className="h-44 md:h-36 w-full bg-gradient-to-br from-green-100 to-green-50 flex items-center justify-center text-strong">
                          <span className="text-lg font-medium">Image</span>
                        </div>
                      )}

                      {/* content */}
                      <div className="p-4 flex-1 flex flex-col justify-between">
                        <div>
                          <h4 className="text-lg font-semibold text-gray-900">{it.name}</h4>
                          <p className="text-sm text-gray-600 mt-2">{it.dis}</p>

                          <div className="mt-3 flex items-center gap-3 text-sm text-gray-500">
                            <span className="text-accent font-semibold">₹{it.price}</span>
                            <span className="mx-1">•</span>
                            <span>⏳ {it.time}</span>
                          </div>
                        </div>

                        <div className="mt-4">
                          <button
                            onClick={() => openWhatsApp(it)}
                            className="w-full px-4 py-2 bg-strong text-white rounded-md text-center hover:opacity-95 transition"
                          >
                            Book on WhatsApp
                          </button>
                        </div>
                      </div>
                    </div>
                  </article>
                );
              })}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
