import { useEffect, useRef, useState } from 'react';
import { WHATSAPP_NUMBER } from '../config';
export default function ServiceCard({ title, subtitle, items }){
  const [open,setOpen] = useState(false);
  const ref = useRef(null);
  useEffect(()=>{
    if(ref.current){
      if(open) ref.current.style.maxHeight = ref.current.scrollHeight + 24 + 'px';
      else ref.current.style.maxHeight = '0px';
    }
  },[open]);
  function book(it){
    const text = `Namaste, main booking karna chahta/chahti hoon: *${it.name}* (Price: ₹${it.price}). Kripya available slots bataiye.`;
    if(!WHATSAPP_NUMBER || WHATSAPP_NUMBER.includes('X')){
      alert('WhatsApp number set nahi hua — src/config.js me WHATSAPP_NUMBER variable me phone number daal dijiye.');
      return;
    }
    window.open(`https://wa.me/${WHATSAPP_NUMBER}?text=${encodeURIComponent(text)}`,'_blank');
  }
  return (
    <article className='bg-white rounded-xl border overflow-hidden transform transition hover:-translate-y-2 hover:shadow-strong'>
      <button onClick={()=>setOpen(o=>!o)} className='w-full text-left px-5 py-4 flex items-center justify-between'>
        <div>
          <h4 className='text-lg font-semibold text-strong'>{title}</h4>
          <p className='text-sm text-gray-500 mt-1'>{subtitle}</p>
        </div>
        <div className='text-accent font-bold text-2xl'>{open ? '−' : '+'}</div>
      </button>
      <div ref={ref} className='px-5 pb-4 collapse' style={{ maxHeight: 0, overflow: 'hidden' }}>
        <div className='mt-3 space-y-3'>
          {items.map((it, idx)=> (
            <div key={idx} className='flex items-center justify-between rounded-md border p-3 bg-gradient-to-b from-white to-gray-50'>
              <div>
                <div className='font-medium text-gray-800'>{it.name}</div>
                <div className='text-accent font-semibold'>₹{it.price}</div>
              </div>
              <button onClick={()=>book(it)} className='px-3 py-2 bg-strong text-white rounded-md'>Book</button>
            </div>
          ))}
        </div>
      </div>
    </article>
  );
}
